create table Shop_Category(
	id_Shop_Category bigint auto_increment,
	image Nvarchar(80),
    id_Category bigint,
    shop_Name nvarchar(255) not null,
    price nvarchar(255) not null,
    description text,
    priority bit,
    primary key (id_Shop_Category),
    constraint lien_ket_1 foreign key(id_Category) references Category(id_Category)
);
create table Category(
	id_Category bigint auto_increment,
    Categoryl nvarchar(30) not null,
    primary key(id_Category)
);
create table Users(
	id_User bigint auto_increment,
    email nvarchar(50) not null,
    username nvarchar(25) not null,
    password nvarchar(255) not null,
    role bit,
    primary key(id_User)
);
create table orders(
	order_id bigint auto_increment,
	id_Shop_Category bigint,
    id_User bigint,
    quantity int,
    order_date datetime,
    primary key(order_id),
 foreign key(id_Shop_Category) references Shop_Category(id_Shop_Category),
     foreign key(id_User) references Users(id_User)
);



